DROP TABLE IF EXISTS `scheduled_email_logs`;
CREATE TABLE `scheduled_email_logs` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '日志ID',
  `task_id` int NOT NULL COMMENT '任务ID',
  `execution_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '执行时间',
  `status` enum('success','failed') COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '执行状态',
  `message` text COLLATE utf8mb4_unicode_ci COMMENT '执行消息',
  `error_details` text COLLATE utf8mb4_unicode_ci COMMENT '错误详情',
  `email_message_id` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '邮件消息ID',
  `execution_duration` int DEFAULT NULL COMMENT '执行耗时(毫秒)',
  `created_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `idx_task_id` (`task_id`),
  KEY `idx_execution_time` (`execution_time`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='定时邮件任务执行日志表';

